import styled from "styled-components";

export const ModalContainer = styled.div`
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 700;
    width: 400px;
    height: 350px;
    outline: 0;

    border-radius: 10px;

    background-color: ${props => props.theme.colors.tertiary};
`;

export const ModalHeader = styled.div`

    height: 50px;

    display: flex;
    flex: 1;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;

`;

export const TitleHeader = styled.p`
    
    font-size: 22px;
    color: ${props => props.theme.colors.white};
`;

export const CloseButton = styled.div`

    font-size: 30px;
    border: none;
    border-radius: 3px;
    margin-left: 0.5rem;
    margin-right: 5px;
    // background: none;
    :hover {
    cursor: pointer;
    }

`;

export const CloseSign = styled.div`

    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    color: #323232;

    &:before,
    &:after {
        position: absolute;
        left: 19px;
        top: 10px;
        content: ' ';
        height: 20px;
        width: 2px;
        background-color: #333;
    }

    &:before {
        transform: rotate(45deg);
    }

    &:after {
        transform: rotate(-45deg)
    }
`;

export const InfoBox = styled.form`
    display: flex;
    flex-direction: row;

    justify-content: space-between;

`;

export const Form = styled.form`

    display: flex;
    align-items: left;
    justify-content: space-between;
    flex-direction: column;


    padding: 30px;

    border-radius: 10px;

    background-color: red;

`;
