import React, {useMemo, useState, useEffect} from 'react';

import { Container, Content, Filters } from './styles';

import ContentHeader from '../../components/ContentHeader';
import SelectInput from '../../components/SelectInput';
import HistoryFinanceCard from '../../components/HistoryFinanceCard';

import gains from '../../repositories/gains';
import expenses from '../../repositories/expenses';

import formatCurrent from '../../utils/formatCurrent';
import formatDate from '../../utils/formatDate';


import { useParams } from 'react-router-dom';

interface IData {
    id: string;
    description: string;
    amountFormatted: string;
    frequency: string;
    dataFormatted: string;
    tagColor: string;
};

const List: React.FC = () => {

    const [data, setData] = useState<IData[]>([]);
    const [monthSelect, setMonthSelected] = useState<string>(String(new Date().getMonth() + 1));
    const [yearSelect, setYearSelected] = useState<string>(String(new Date().getMonth() + 1));
    const [selectedFrequency, setSelectedFrequency] = useState(['recorrente', 'eventual']);

    let params = useParams();

    const title = useMemo(() => {
        return params.type === 'entry-balance' ? 'Entradas' : 'Saídas'
    }, [params.type]);

    const lineColor = useMemo(() => {
        return params.type === 'entry-balance' ? '#f7931b' : '#e44c4e'
    }, [params.type]);

    const listData = useMemo(() => {
        return params.type === 'entry-balance' ? gains : expenses;
    },[]);

    const months = [
        {value: 1, label: 'Janeiro'},
        {value: 2, label: 'Fevereiro'},
        {value: 3, label: 'Março'},
        {value: 4, label: 'Abril'},
        {value: 5, label: 'Maio'},
        {value: 6, label: 'Junho'},
        {value: 7, label: 'Julho'},
        {value: 8, label: 'Agosto'},
        {value: 9, label: 'Setembro'},
        {value: 10, label: 'Outubro'},
        {value: 11, label: 'Março'},
        {value: 12, label: 'Dezembro'},
    ]

    const years = [
        {value: 2021, label: 2021},
        {value: 2020, label: 2020},
        {value: 2022, label: 2022},
    ];

    const handleFrequencyClick = (frequency: string) => {

        const alreadySelected = selectedFrequency.findIndex(item => item === frequency);

        if(alreadySelected >= 0){
            console.log('Ja esta selecionado')
        } else {
            setSelectedFrequency([frequency]);
            console.log('Frequencia selecionada agora')
        }
        
    }

    useEffect(() => {

        const filteredDate = listData.filter(item => {

            const date = new Date(item.date);
            const month = String(date.getMonth() + 1);
            const year = String(date.getFullYear());

            return month === monthSelect && year === yearSelect;
        });

        const formattedData = filteredDate.map(item => {

            return {
                id: String(Math.random() * filteredDate.length),
                description: item.description,
                amountFormatted: formatCurrent(Number(item.amount)),
                frequency: item.frequency,
                dataFormatted: formatDate(item.date),
                tagColor: item.frequency === 'recorrente' ? '#4e41f0' : '#e44c4e'
            }
        })
        setData(formattedData);
    },[listData, monthSelect, yearSelect]);

    return (
        <Container>
            <ContentHeader title={title} lineColor={lineColor}>
                <SelectInput options={months} onChange={(e) => setMonthSelected(e.target.value)} defaultValue={monthSelect}/>
                <SelectInput options={years} onChange={(e) => setYearSelected(e.target.value)} defaultValue={yearSelect}/>
            </ContentHeader>

            <Filters>
                <button 
                    type="button"
                    className="tag-filter tag-filter-recurrent"
                    onClick={() => handleFrequencyClick('recorrente')}
                >
                    Recorrentes
                </button>
                <button 
                    type="button"
                    className="tag-filter tag-filter-eventual"
                    onClick={() => handleFrequencyClick('eventual')}
                >
                    Eventuais
                </button>
            </Filters>

            <Content>
                {
                    data.map(item => (
                        <HistoryFinanceCard
                            key={item.id}
                            tagColor={item.tagColor}
                            title={item.description}
                            subtitle={item.dataFormatted}
                            amount={item.amountFormatted}
                        />
                    ))
                    
                }
            </Content>
        </Container>
    )
}

export default List;
