import React, { useState } from "react";
import ReactDOM  from 'react-dom';
import Button from "../../Button";
import Input from "../../input";

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import {
    ModalContainer,
    ModalHeader,
    CloseButton,
    Form,
    InfoBox,
    TitleHeader,
    DatePickerContainer,
    ButtonContainer,
} from './styles';

interface IModalProps {
    isModalVisible: boolean;
    hideModal: () => void;
}

const AddFlowModal: React.FC<IModalProps> = ({
    isModalVisible,
    hideModal
}) => {

    const [description, setDescription] =useState<string>('');
    const [amount, setAmount] = useState<string>('');
    const [flowType, setFlowType] = useState<string>('');
    const [frequency, setFrequency] = useState<string>('');
    const [flowDate, setFlowDate] = useState(new Date());

    const modal = (
        <ModalContainer>
            <ModalHeader>
                <TitleHeader>Adicionar Fluxo Financeiro!</TitleHeader>
                <CloseButton onClick={hideModal}>X</CloseButton>
            </ModalHeader>

            <Form>
                <InfoBox>
                    <label>Descrição:</label>
                    <input
                        type='textarea'
                        placeholder='Descrição'
                        required
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </InfoBox>

                <InfoBox>
                    <label>Quantia:</label>
                    <input
                        type='text'
                        placeholder='R$0.00'
                        required
                        onChange={(e) => setAmount(e.target.value)}
                    />
                </InfoBox>

                <InfoBox>
                    <label>Tipo do fluxo:</label>
                    <select 
                        onChange={(e) => setFlowType(e.target.value)} 
                        placeholder='Tipo'
                        required>
                        <option value='entry'>Entrada</option>
                        <option value='exit'>Saída</option>
                    </select>
                </InfoBox>

                <InfoBox>
                    <label>Selecione a frequencia:</label>
                    <select onChange={(e) => setFrequency(e.target.value)}>
                        <option value='recurrent'>Recorrente</option>
                        <option value='eventual'>Eventual</option>
                    </select>
                </InfoBox>

                <InfoBox>
                    <label>Selecione a data:</label>
                    <DatePickerContainer>
                        <DatePicker
                            selected={flowDate}
                            onSelect={(date:Date) => setFlowDate(date)}
                            onChange={(date:Date) => setFlowDate(date)}
                            dateFormat="dd/MM/yyyy"
                        />
                    </DatePickerContainer>
                </InfoBox>

                <ButtonContainer>
                    <Button type='submit'>Adicionar</Button>
                </ButtonContainer>
            </Form>
        </ModalContainer>
    );

    return isModalVisible ? ReactDOM.createPortal(modal, document.body) : null;
}

export default AddFlowModal;
