import React, { useState } from "react";
import ReactDOM  from 'react-dom';
import Button from "../Button";
import Input from "../input";

import DatePicker from "react-datepicker";

import {
    ModalContainer,
    ModalHeader,
    CloseButton,
    CloseSign,
    Form,
} from './styles';

interface IModalProps {
    isModalVisible: boolean;
    hideModal: () => void;
}

const AddFlowModal: React.FC<IModalProps> = ({
    isModalVisible,
    hideModal
}) => {

    const [description, setDescription] =useState<string>('');
    const [amount, setAmount] = useState<string>('');
    const [flowType, setFlowType] = useState<string>('');
    const [frequency, setFrequency] = useState<string>('');
    const [flowDate, setFlowDate] = useState(new Date());

    const modal = (
        <ModalContainer>
            <ModalHeader>
                <p>Insira as informações a seguir para adicionar um Novo Fluxo Financeiro!</p>
                <CloseButton>
                    <CloseSign onClick={hideModal}/>
                </CloseButton>
            </ModalHeader>

            <Form>
                <input
                    type='text'
                    placeholder='Descrição'
                    required
                    onChange={(e) => setDescription(e.target.value)}
                />

                <input
                    type='text'
                    placeholder='Valor'
                    required
                    onChange={(e) => setAmount(e.target.value)}
                />

                <select onChange={(e) => setFlowType(e.target.value)}>
                    <option value='entry'>Entrada</option>
                    <option value='exit'>Saída</option>
                </select>

                <select onChange={(e) => setFrequency(e.target.value)}>
                    <option value='recurrent'>Recorrente</option>
                    <option value='eventual'>Eventual</option>
                </select>

                <DatePicker
                    selected={flowDate}
                    onSelect={(date:Date) => setFlowDate(date)}
                    onChange={(date:Date) => setFlowDate(date)}
                />

                <Button type='submit'>Enviar</Button>
            </Form>
        </ModalContainer>
    );

    return isModalVisible ? ReactDOM.createPortal(modal, document.body) : null;
}

export default AddFlowModal;
