import React, {useMemo, useState, useEffect} from 'react';

import { Container, Content, Filters, AddButtonContainer } from './styles';

import ContentHeader from '../../components/ContentHeader';
import SelectInput from '../../components/SelectInput';
import HistoryFinanceCard from '../../components/HistoryFinanceCard';
import AddButton from '../../components/AddButton';

import axios from "axios";

import gains from '../../repositories/gains';
import expenses from '../../repositories/expenses';

import formatCurrent from '../../utils/formatCurrent';
import formatDate from '../../utils/formatDate';

import AddFlowModal from '../../components/Modals/AddFlowModal';
import { useModal } from '../../components/Modals/AddFlowModal/useModal';

import { useParams } from 'react-router-dom';

interface IData {
    id: string;
    description: string;
    amountFormatted: string;
    frequency: string;
    dateFormatted: string;
    tagColor: string;
};

const List: React.FC = () => {

    const [data, setData] = useState<IData[]>([]);
    const [monthSelect, setMonthSelected] = useState<number>(new Date().getMonth() + 1);
    const [yearSelect, setYearSelected] = useState<number>(new Date().getFullYear());
    const [frequencyFilterSelected, setfrequencyFilterSelected] = useState(['recorrente', 'eventual']);

    let params = useParams();

    const pageData = useMemo(() => {
        return params.type === 'entry-balance' ?
        {
            title: 'Entradas',
            lineColor: '#4e41f0',
            data: gains
        }
        :
        {
            title: 'Saídas',
            lineColor: '#e44c4e',
            data: expenses
        }
    }, [params.type]);

    const months = [
        {value: 1, label: 'Janeiro'},
        {value: 2, label: 'Fevereiro'},
        {value: 3, label: 'Março'},
        {value: 4, label: 'Abril'},
        {value: 5, label: 'Maio'},
        {value: 6, label: 'Junho'},
        {value: 7, label: 'Julho'},
        {value: 8, label: 'Agosto'},
        {value: 9, label: 'Setembro'},
        {value: 10, label: 'Outubro'},
        {value: 11, label: 'Março'},
        {value: 12, label: 'Dezembro'},
    ]

    const years = [
        {value: 2021, label: 2021},
        {value: 2020, label: 2020},
        {value: 2022, label: 2022},
    ];

    const handleFrequencyClick = (frequency: string) => {

        const alreadySelected = frequencyFilterSelected.findIndex(item => item === frequency);

        if(alreadySelected >= 0){
            const filtered = frequencyFilterSelected.filter(item => item !== frequency);
            setfrequencyFilterSelected(filtered);
        } else {
            setfrequencyFilterSelected((prev) => [...prev, frequency]);
        }   
    };

    const handleMonthSelected = (month : string) => {
        try {
            const parseMonth = Number(month);
            setMonthSelected(parseMonth);
        } catch (error) {
            throw new Error('invalid month value; Is accept 0 - 12.');
        }
    };

    const handleYearSelected = (year : string) => {
        try {
            const parseYear = Number(year);
            setYearSelected(parseYear);
        } catch (error) {
            throw new Error('invalid Year value');
        }
    };

    const { isShown, toggle } = useModal();

    useEffect(() => {

        const filteredDate = pageData.data.filter(item => {

            const date = new Date(item.date);
            const month = date.getMonth() + 1;
            const year = date.getFullYear();

            return month === monthSelect && year === yearSelect && frequencyFilterSelected.includes(item.frequency);
        });

        const formattedData = filteredDate.map(item => {

            return {
                id: String(Math.random() * filteredDate.length),
                description: item.description,
                amountFormatted: formatCurrent(Number(item.amount)),
                frequency: item.frequency,
                dateFormatted: formatDate(item.date),
                tagColor: item.frequency === 'recorrente' ? '#4e41f0' : '#e44c4e'
            }
        })
        setData(formattedData);
    },[pageData.data, monthSelect, yearSelect, frequencyFilterSelected]);

    return (
        <Container>
            <ContentHeader title={pageData.title} lineColor={pageData.lineColor}>
                <SelectInput
                    options={months}
                    onChange={(e) => handleMonthSelected(e.target.value)}
                    defaultValue={monthSelect}
                />
                <SelectInput
                    options={years}
                    onChange={(e) => handleYearSelected(e.target.value)}
                    defaultValue={yearSelect}
                />
            </ContentHeader>

            <AddButtonContainer>
                <AddButton onClick={toggle}>Adicionar novo Fluxo Financeiro</AddButton>
                <AddFlowModal
                    isModalVisible={isShown}
                    hideModal={toggle}
                />
                    
            </AddButtonContainer>

            <Filters>
                <button 
                    type="button"
                    className={`tag-filter tag-filter-recurrent
                    ${frequencyFilterSelected.includes('recorrente') && 'tag-actived'}`}
                    onClick={() => handleFrequencyClick('recorrente')}
                >
                    Recorrentes
                </button>
                <button 
                    type="button"
                    className={`tag-filter tag-filter-eventual
                    ${frequencyFilterSelected.includes('eventual') && 'tag-actived'}`}
                    onClick={() => handleFrequencyClick('eventual')}
                >
                    Eventuais
                </button>
            </Filters>

            <Content>
                {
                    data.map(item => (
                        <HistoryFinanceCard
                            key={item.id}
                            tagColor={item.tagColor}
                            title={item.description}
                            subtitle={item.dateFormatted}
                            amount={item.amountFormatted}
                        />
                    ))
                    
                }
            </Content>
        </Container>
    )
}

export default List;
