import React from 'react';

import {
    ResponsiveContainer,
    BarChart,
    Bar,
    Cell,
    Tooltip
} from 'recharts';

import formatCurrent from '../../utils/formatCurrent';

import {
    Container,
    SideLeft,
    SideRight
} from './styles';

interface IBarChartProps {
    title: string,
    data: {
        name: string;
        amount: number;
        percent: number;
        color: string
    }[],
}

const BarCharBox: React.FC<IBarChartProps> = ({
    title, data
}) => {
    return (
        <Container>
            <SideLeft>
                <h2>{title}</h2>
            </SideLeft>

            <SideRight>
                <ResponsiveContainer>
                    <BarChart data={data}>
                        <Bar dataKey="amount">
                            {
                                data.map((indicator) => (
                                    <Cell 
                                        key={indicator.name}
                                        fill={indicator.color}
                                    />
                                ))
                            }
                        <Tooltip formatter={(value: any) => formatCurrent(Number(value))} />
                        </Bar>
                    </BarChart>
                </ResponsiveContainer>
            </SideRight>
        </Container>
    );
}

export default BarCharBox;
