import styled from "styled-components";

interface IConteinerProps {
    color: string;
}

export const Container = styled.div<IConteinerProps>`

    width: 32%;
    height: 150px;

    margin: 10px 0;

    background-color: ${props => props.color};
    color: ${props => props.theme.colors.white};

    border-radius: 7px;
    padding: 10px 20px;

    position: relative;
    overflow: hidden;

    > img {
        height: 110%;

        position: absolute;
        top: -10px;
        right: -10px;

        opacity: .3;
    }
`;
